package net.yasite.searchperson.dao;import java.util.Map;import net.yasite.searchperson.entity.AddressEntitiy;import android.database.sqlite.SQLiteDatabase;import de.greenrobot.dao.AbstractDao;import de.greenrobot.dao.AbstractDaoSession;import de.greenrobot.dao.identityscope.IdentityScopeType;import de.greenrobot.dao.internal.DaoConfig;public class DaoSession extends AbstractDaoSession {	private final DaoConfig addressDaoConfig;	private final AddressDao addressDao;		public DaoSession(SQLiteDatabase db, IdentityScopeType type, Map<Class<? extends AbstractDao<?, ?>>, DaoConfig> daoConfigMap) {		super(db);		addressDaoConfig = daoConfigMap.get(AddressDao.class).clone();		addressDaoConfig.initIdentityScope(type);		addressDao = new AddressDao(addressDaoConfig,this);		registerDao(AddressEntitiy.class, addressDao);	}	public void clear() {		addressDaoConfig.getIdentityScope().clear();	}}